<?phpinclude_once("dbconfig.php");include_once("functions.php");function addCalendar($st, $et, $sub, $ade){  $ret = array();  try{    $db = new DBConnection();    $db->getConnection();    $sql = "insert into `jqcalendar` (`subject`, `starttime`, `endtime`, `isalldayevent`) values ('"      .mysql_real_escape_string($sub)."', '"      .php2MySqlTime(js2PhpTime($st))."', '"      .php2MySqlTime(js2PhpTime($et))."', '"      .mysql_real_escape_string($ade)."' )";    //echo($sql);		if(mysql_query($sql)==false){      $ret['IsSuccess'] = false;      $ret['Msg'] = mysql_error();    }else{      $ret['IsSuccess'] = true;      $ret['Msg'] = 'Event added successfully.';      $ret['Data'] = mysql_insert_id();      // -- CUSTOM: Add course / subject relationship link --      session_start();      if (isset($_SESSION['course']['course_id']))			$sql = "INSERT INTO event_rel_course VALUES (".$ret['Data'].", ".$_SESSION['course']['course_id'].");";		else if (isset($_SESSION['subject']['subject_id']))			$sql = "INSERT INTO event_rel_subject VALUES (".$ret['Data'].", ".$_SESSION['subject']['subject_id'].");";		mysql_query($sql);    }	}catch(Exception $e){     $ret['IsSuccess'] = false;     $ret['Msg'] = $e->getMessage();  }  return $ret;}function addDetailedCalendar($st, $et, $sub, $ade, $dscr, $loc, $color, $tz){  $ret = array();  try{    $db = new DBConnection();    $db->getConnection();    $sql = "insert into `jqcalendar` (`subject`, `starttime`, `endtime`, `isalldayevent`, `description`, `location`, `color`) values ('"      .mysql_real_escape_string($sub)."', '"      .php2MySqlTime(js2PhpTime($st))."', '"      .php2MySqlTime(js2PhpTime($et))."', '"      .mysql_real_escape_string($ade)."', '"      .mysql_real_escape_string($dscr)."', '"      .mysql_real_escape_string($loc)."', '"      .mysql_real_escape_string($color)."' )";    //echo($sql);		if(mysql_query($sql)==false){      $ret['IsSuccess'] = false;      $ret['Msg'] = mysql_error();    }else{      $ret['IsSuccess'] = true;      $ret['Msg'] = 'Event added successfully.';      $ret['Data'] = mysql_insert_id();      // -- CUSTOM: Add course / subject relationship link --      session_start();      if (isset($_SESSION['course']['course_id']))			$sql = "INSERT INTO event_rel_course VALUES (".$ret['Data'].", ".$_SESSION['course']['course_id'].");";		else if (isset($_SESSION['subject']['subject_id']))			$sql = "INSERT INTO event_rel_subject VALUES (".$ret['Data'].", ".$_SESSION['subject']['subject_id'].");";		mysql_query($sql);    }	}catch(Exception $e){     $ret['IsSuccess'] = false;     $ret['Msg'] = $e->getMessage();  }  return $ret;}function listCalendarByRange($sd, $ed){  $ret = array();  $ret['events'] = array();  $ret["issort"] =true;  $ret["start"] = php2JsTime($sd);  $ret["end"] = php2JsTime($ed);  $ret['error'] = null;  try{    $db = new DBConnection();    $db->getConnection();    // -- Original SQL --    //$sql = "select * from `jqcalendar` where `starttime` between '"    //  .php2MySqlTime($sd)."' and '". php2MySqlTime($ed)."'";    // -- Custom SQL --    session_start();    if (isset($_SESSION['course']['course_id']))    {		 $sql = "SELECT * FROM jqcalendar					INNER JOIN event_rel_course ON jqcalendar.Id = event_rel_course.event_id					WHERE course_id = ".$_SESSION['course']['course_id']." AND `starttime` between '".php2MySqlTime($sd)."' AND '". php2MySqlTime($ed)."'";	 }	 else if (isset($_SESSION['subject']['subject_id']))	 {		 $sql = "SELECT * FROM jqcalendar					INNER JOIN event_rel_subject ON jqcalendar.Id = event_rel_subject.event_id					WHERE subject_id = ".$_SESSION['subject']['subject_id']." AND `starttime` between '".php2MySqlTime($sd)."' AND '". php2MySqlTime($ed)."'";	 }    $handle = mysql_query($sql);    //echo $sql;    while ($row = mysql_fetch_object($handle)) {      //$ret['events'][] = $row;      //$attends = $row->AttendeeNames;      //if($row->OtherAttendee){      //  $attends .= $row->OtherAttendee;      //}      //echo $row->StartTime;      $ret['events'][] = array(        $row->Id,        $row->Subject,        php2JsTime(mySql2PhpTime($row->StartTime)),        php2JsTime(mySql2PhpTime($row->EndTime)),        $row->IsAllDayEvent,        0, //more than one day event        //$row->InstanceType,        0,//Recurring event,        $row->Color,        1,//editable        $row->Location,        ''//$attends      );    }	}catch(Exception $e){     $ret['error'] = $e->getMessage();  }  return $ret;}function listCalendar($day, $type){  $phpTime = js2PhpTime($day);  //echo $phpTime . "+" . $type;  switch($type){    case "month":      $st = mktime(0, 0, 0, date("m", $phpTime), 1, date("Y", $phpTime));      $et = mktime(0, 0, -1, date("m", $phpTime)+1, 1, date("Y", $phpTime));      break;    case "week":      //suppose first day of a week is monday      $monday  =  date("d", $phpTime) - date('N', $phpTime) + 1;      //echo date('N', $phpTime);      $st = mktime(0,0,0,date("m", $phpTime), $monday, date("Y", $phpTime));      $et = mktime(0,0,-1,date("m", $phpTime), $monday+7, date("Y", $phpTime));      break;    case "day":      $st = mktime(0, 0, 0, date("m", $phpTime), date("d", $phpTime), date("Y", $phpTime));      $et = mktime(0, 0, -1, date("m", $phpTime), date("d", $phpTime)+1, date("Y", $phpTime));      break;  }  //echo $st . "--" . $et;  return listCalendarByRange($st, $et);}function updateCalendar($id, $st, $et){  $ret = array();  try{    $db = new DBConnection();    $db->getConnection();    $sql = "update `jqcalendar` set"      . " `starttime`='" . php2MySqlTime(js2PhpTime($st)) . "', "      . " `endtime`='" . php2MySqlTime(js2PhpTime($et)) . "' "      . "where `id`=" . $id;    //echo $sql;		if(mysql_query($sql)==false){      $ret['IsSuccess'] = false;      $ret['Msg'] = mysql_error();    }else{      $ret['IsSuccess'] = true;      $ret['Msg'] = 'Succefully';    }	}catch(Exception $e){     $ret['IsSuccess'] = false;     $ret['Msg'] = $e->getMessage();  }  return $ret;}function updateDetailedCalendar($id, $st, $et, $sub, $ade, $dscr, $loc, $color, $tz){  $ret = array();  try{    $db = new DBConnection();    $db->getConnection();    $sql = "update `jqcalendar` set"      . " `starttime`='" . php2MySqlTime(js2PhpTime($st)) . "', "      . " `endtime`='" . php2MySqlTime(js2PhpTime($et)) . "', "      . " `subject`='" . mysql_real_escape_string($sub) . "', "      . " `isalldayevent`='" . mysql_real_escape_string($ade) . "', "      . " `description`='" . mysql_real_escape_string($dscr) . "', "      . " `location`='" . mysql_real_escape_string($loc) . "', "      . " `color`='" . mysql_real_escape_string($color) . "' "      . "where `id`=" . $id;    //echo $sql;		if(mysql_query($sql)==false){      $ret['IsSuccess'] = false;      $ret['Msg'] = mysql_error();    }else{      $ret['IsSuccess'] = true;      $ret['Msg'] = 'Event updated successfully.';    }	}catch(Exception $e){     $ret['IsSuccess'] = false;     $ret['Msg'] = $e->getMessage();  }  return $ret;}function removeCalendar($id){  $ret = array();  try{    $db = new DBConnection();    $db->getConnection();    $sql = "delete from `jqcalendar` where `id`=" . $id;		if(mysql_query($sql)==false){      $ret['IsSuccess'] = false;      $ret['Msg'] = mysql_error();    }else{      $ret['IsSuccess'] = true;      $ret['Msg'] = 'Event removed.';      // -- CUSTOM: Remove course / subject relationship link --		$sql = "DELETE FROM event_rel_course WHERE event_id = ".$id;		mysql_query($sql);		$sql = "DELETE FROM event_rel_subject WHERE event_id = ".$id;		mysql_query($sql);    }	}catch(Exception $e){     $ret['IsSuccess'] = false;     $ret['Msg'] = $e->getMessage();  }  return $ret;}header('Content-type:text/javascript;charset=UTF-8');$method = $_GET["method"];switch ($method) {    case "add":        $ret = addCalendar($_POST["CalendarStartTime"], $_POST["CalendarEndTime"], $_POST["CalendarTitle"], $_POST["IsAllDayEvent"]);        break;    case "list":        $ret = listCalendar($_POST["showdate"], $_POST["viewtype"]);        break;    case "update":        $ret = updateCalendar($_POST["calendarId"], $_POST["CalendarStartTime"], $_POST["CalendarEndTime"]);        break;    case "remove":        $ret = removeCalendar( $_POST["calendarId"]);        break;    case "adddetails":        $st = $_POST["stpartdate"] . " " . $_POST["stparttime"];        $et = $_POST["etpartdate"] . " " . $_POST["etparttime"];        if(isset($_GET["id"])){            $ret = updateDetailedCalendar($_GET["id"], $st, $et,                $_POST["Subject"], isset($_POST["IsAllDayEvent"])?1:0, $_POST["Description"],                $_POST["Location"], $_POST["colorvalue"], $_POST["timezone"]);        }else{            $ret = addDetailedCalendar($st, $et,                $_POST["Subject"], isset($_POST["IsAllDayEvent"])?1:0, $_POST["Description"],                $_POST["Location"], $_POST["colorvalue"], $_POST["timezone"]);        }        break;}echo json_encode($ret);?>